			});
			const checkmode = localStorage.getItem('drag_mode_onoff');
			if(checkmode == 1){
				document.getElementById('drag_mode').href=drag_style;
				elements[0].checked = true;
			}else{
				document.getElementById('drag_mode').href='#';
				elements[0].checked = false;
